            <Button size="sm" color="primary" tag={Link} to={"/categorias/" + categoria.idCategoria}>Edit</Button>
            <Button size="sm" color="danger" onClick={() => this.remove(categoria.idCategoria)}>Delete</Button>
        </ButtonGroup>
    </td>
</tr>
});

    return (
        <div className="App">
          <header className="App-header">

            <div className="App-intro">
              <h2>Categorias</h2>
              {categorias.map(categorias =>
                  <div key={categorias.idCategoria}>
                    {categorias.idCategoria} ({categorias.descricaoCategoria})
                  </div>
              )}
            </div>
          </header>
        </div>
    );
  }
}



export default App;
