//import logo from './logo.svg';
import './App.css';
//import ReclamacoesComponent from './components/ReclamacoesComponent';
import CategoriasComponent from './components/CategoriasComponent';
import React, { Component } from 'react'
import { Button, ButtonGroup, Container, Table } from 'reactstrap';
import { Link } from 'react-router-dom';




class App extends Component {
  state = {
    categorias: []
  };

  async componentDidMount() {
    const response = await fetch('http://localhost:8082/safecity/v1/categorias');
    const body = await response.json();
    this.setState({categorias: body});
  }
  async remove(id) {
    await fetch(`/categorias/${id}`, {
        method: 'DELETE',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        }
    }).then(() => {
        let updatedCategorias = [...this.state.categorias].filter(i => i.id !== id);
        this.setState({categorias: updatedCategorias});
    });
  }

  render() {
    const {categorias, isLoading} = this.state;

    if (isLoading) {
      return <p>Loading...</p>;
  }

  const categoriasList = categorias.map(categoria => {
    return <tr key={categoria.id}>
    <td style={{whiteSpace: 'nowrap'}}>{categoria.idCategoria}</td>
    <td>{categoria.descricaoCategoria}</td>
    <td>
        <ButtonGroup>
            <Button size="sm" color="primary" tag={Link} to={"/categorias/" + categoria.idCategoria}>Edit</Button>
            <Button size="sm" color="danger" onClick={() => this.remove(categoria.idCategoria)}>Delete</Button>
        </ButtonGroup>
    </td>
</tr>
});

    return  (
      <div>
          <AppNavbar/>
          <Container fluid>
              <div className="float-right">
                  <Button color="success" tag={Link} to="/categorias/new">Add Client</Button>
              </div>
              <h3>Categorias</h3>
              <Table className="mt-4">
                  <thead>
                  <tr>
                      <th width="30%">Name</th>
                      <th width="30%">Email</th>
                      <th width="40%">Actions</th>
                  </tr>
                  </thead>
                  <tbody>
                  {categoriasList}
                  </tbody>
              </Table>
          </Container>
      </div>
  );
  }
}



export default App;
