import React, { Component } from 'react';
import AppNavbar from './AppNavBar';
import { Link } from 'react-router-dom';
import { ButtonGroup, Container, Button, Table } from 'reactstrap';
//import AppNavbar from './AppNavBar';

class CategoriaList extends Component{

//    constructor(props){
//        super(props)
//        this.state = {categorias: []}
//        this.remove = this.remove.bind(this);
//    }

constructor(props) {
    super(props);
    this.state = {
        selectedValue: "myself",
        destinations: ["myself","somebody"],
    };
}

    componentDidMount(){
        console.log('passou')
        fetch('/categorias')
            .then(response => response.json())
            .then(data => this.setState({categorias: data}));
    }

    async remove(id) {
        await fetch(`/categorias/${id}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
            let updatedCategorias = [...this.state.categorias].filter(i => i.id !== id);
            this.setState({categorias: updatedCategorias});
        });
    }

render() {
    const {categorias, isLoading} = this.state;

    if (isLoading) {
        return <p>Loading...</p>;
    }

    const categoriaList = categorias.map(categoria => {
        return <tr key={categoria.idCategoria}>
            <td style={{whiteSpace: 'nowrap'}}>{categoria.descricaoCategoria}</td>
            <td>{categoria.descricaoCategoria}</td>
            <td>
                <ButtonGroup>
                    <Button size="sm" color="primary" tag={Link} to={"/categorias/" + categoria.idCategoria}>Edit</Button>
                    <Button size="sm" color="danger" onClick={() => this.remove(categoria.idCategoria)}>Delete</Button>
                </ButtonGroup>
            </td>
        </tr>
    });

    return (
        <div>
            <AppNavbar/>
            <Container fluid>
                <div className="float-right">
                    <Button color="success" tag={Link} to="/categorias/new">Add Client</Button>
                </div>
                <h3>Categoria</h3>
                <Table className="mt-4">
                    <thead>
                    <tr>
                        <th width="30%">Name</th>
                        <th width="30%">Email</th>
                        <th width="40%">Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                    {categoriaList}
                    </tbody>
                </Table>
            </Container>
        </div>
    );
}

}

export default CategoriaList;
