import React from 'react';
import CategoriasService from '../services/CategoriasService';


class CategoriasComponent extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            categorias:[]
        }
    }

    componentDidMount(){
        CategoriasService.getCategorias().then((response) => {
            this.setState({ categorias: response.data})
        });
    }

    render(){
        return (
            <form class="form-floating">
            <div>
                <h1 className = "text-right"> Lista de Categorias</h1>
                <table className = "table table-striped">
                    <thead>
                        <tr class="d-flex">
                            <td class="col-2">Id</td>
                            <td class="col-2">Descrição</td>
                        </tr>
                    </thead>
                    <tbody>                      
                        {
                            this.state.categorias.map(
                                categoria =>
                                <tr key = {categoria.idCategoria} >
                                    <td class="col-2"> {categoria.idCategoria} </td>
                                    <td class="col-2"> {categoria.descricaoCategoria} </td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
            </form>
        )
    }
}

export default CategoriasComponent
