//import logo from './logo.svg';
import './App.css';
//import ReclamacoesComponent from './components/ReclamacoesComponent';
import CategoriasComponent from './components/CategoriasComponent';
import React, { Component } from 'react'


class App extends Component {
  state = {
    categorias: []
  };

  async componentDidMount() {
    const response = await fetch('http://localhost:8082/safecity/v1/categorias');
    const body = await response.json();
    this.setState({categorias: body});
  }
  async remove(id) {
    await fetch(`/categorias/${id}`, {
        method: 'DELETE',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        }
    }).then(() => {
        let updatedClients = [...this.state.clients].filter(i => i.id !== id);
        this.setState({categorias: updatedClients});
    });
  }

  render() {
    const {categorias, isLoading} = this.state;

    if (isLoading) {
      return <p>Loading...</p>;
  }

    return (
        <div className="App">
          <header className="App-header">

            <div className="App-intro">
              <h2>Categorias</h2>
              {categorias.map(categorias =>
                  <div key={categorias.idCategoria}>
                    {categorias.idCategoria} ({categorias.descricaoCategoria})
                  </div>
              )}
            </div>
          </header>
        </div>
    );
  }
}



export default App;
